def count_pairs_to_remove(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    k = total_sum / n

    if k * 2 == total_sum:
        return n * (n - 1) // 2

    count = 0
    element_count = {}
    for num in arr:
        element_count[num] = element_count.get(num, 0) + 1

    for i in range(n):
        for j in range(i + 1, n):
            new_sum = total_sum - arr[i] - arr[j]
            if new_sum == k * (n - 2):
                count += 1
    return count

