def can_draw_ticks(n: int, m: int, k: int, field: List[List[str]]) -> str:
    def is_valid_tick(i: int, j: int, d: int) -> bool:
        if i - d < 0 or i + d >= n or j - d < 0 or j + d >= m:
            return False
        for h in range(d + 1):
            if field[i - h][j - h] != '*' or field[i - h][j + h] != '*' or field[i + h][j - h] != '*' or field[i + h][j + h] != '*':
                return False
        return True

    def mark_tick(i: int, j: int, d: int) -> None:
        for h in range(d + 1):
            field[i - h][j - h] = '*'
            field[i - h][j + h] = '*'
            field[i + h][j - h] = '*'
            field[i + h][j + h] = '*'
            field[i][j] = '*'

    for i in range(n):
        for j in range(m):
            if field[i][j] == '*':
                d = k
                while d <= min(i, j, n - 1 - i, m - 1 - j):
                    if is_valid_tick(i, j, d):
                        mark_tick(i, j, d)
                    d += 1

    for i in range(n):
        for j in range(m):
            if field[i][j] == '.':
                return 'NO'
    return 'YES'

