def find_perfect_sequences(n: int, M: int) -> int:
    def is_good_sequence(sequence: list) -> bool:
        max_val = max(sequence)
        min_val = min(sequence)
        sum_val = sum(sequence)
        return max_val * min_val >= sum_val

    def count_sequences(n: int, M: int) -> int:
        result = 0
        for length in range(1, n + 1):
            for subset in range(1 << length):
                sequence = [1] * (n + 1)
                for i in range(length):
                    if subset & (1 << i):
                        sequence[i + 1] = 2
                if is_good_sequence(sequence):
                    result = (result + 1) % M
        return result

    return count_sequences(n, M)

