def fill_mosaic(n: int, grid: List[str]) -> Optional[str]:
    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < n

    def dfs(x, y, color):
        stack = [(x, y)]
        tiles = []
        while stack:
            cx, cy = stack.pop()
            if not is_valid(cx, cy) or grid[cx][cy] != color:
                continue
            grid[cx] = grid[cx][:cy] + 'X' + grid[cx][cy+1:]
            tiles.append((cx, cy))
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = cx + dx, cy + dy
                if is_valid(nx, ny) and grid[nx][ny] == color:
                    stack.append((nx, ny))
        return tiles

    def count_adjacent_tiles(tiles):
        count = 0
        for x, y in tiles:
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny) and grid[nx][ny] == grid[x][y]:
                    count += 1
        return count

    def fill_tiles(tiles, color):
        for x, y in tiles:
            grid[x] = grid[x][:y] + color + grid[x][y+1:]

    # Check for initial invalid states
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'G' or grid[i][j] == 'S':
                if count_adjacent_tiles([(i, j)]) != 2:
                    return "NONE"

    # Try to find a unique solution
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'G' or grid[i][j] == 'S':
                tiles = dfs(i, j, grid[i][j])
                if not tiles:
                    continue
                # Check if filling with the opposite color results in a unique solution
                opposite_color = 'S' if grid[i][j] == 'G' else 'G'
                fill_tiles(tiles, opposite_color)
                if all(grid[x][y] == opposite_color for x, y in tiles):
                    # Check if the solution is unique
                    unique = True
                    for x in range(n):
                        for y in range(n):
                            if grid[x][y] == '.':
                                new_tiles = dfs(x, y, opposite_color)
                                if new_tiles:
                                    if count_adjacent_tiles(new_tiles) == 2:
                                        unique = False
                                        break
                        if not unique:
                            break
                    if unique:
                        fill_tiles(tiles, 'G' if grid[i][j] == 'S' else 'S')
                        return "UNIQUE\n" + "\n".join(grid)
                    else:
                        fill_tiles(tiles, grid[i][j])
                else:
                    fill_tiles(tiles, grid[i][j])

    return "MULTIPLE"

