def find_beautiful_coloring(n: int, a: List[int]) -> int:
    # Check if it's possible to color the array beautifully
    def is_beautiful(d: int) -> bool:
        color = [0] * n
        for i in range(n):
            if a[i] % d == 0:
                color[i] = 1
        for i in range(1, n):
            if color[i] == color[i - 1]:
                return False
        return True

    # Try to find a beautiful coloring by checking divisors of the differences
    differences = set()
    for i in range(n):
        for j in range(i + 1, n):
            differences.add(abs(a[i] - a[j]))

    for d in differences:
        if is_beautiful(d):
            return d
    return 0

