def find_max_triangle_area(t, test_cases):
    def calculate_area(x1, y1, x2, y2, x3, y3):
        return abs((x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2)) / 2)

    def is_valid_triangle(x1, y1, x2, y2, x3, y3):
        return (x1 != x2 or y1 != y2) and (x1 != x3 or y1 != y3) and (x2 != x3 or y2 != y3)

    def is_lattice_point(x, y, w, h):
        return 0 < x < w and 0 < y < h

    max_areas = []

    for w, h, x_coords, y_coords in test_cases:
        max_area = 0
        for i in range(len(x_coords)):
            for j in range(i + 1, len(x_coords)):
                x1, x2 = x_coords[i], x_coords[j]
                for k in range(len(y_coords)):
                    for l in range(k + 1, len(y_coords)):
                        y1, y2 = y_coords[k], y_coords[l]
                        if is_lattice_point(x1, y1, w, h) and is_lattice_point(x2, y2, w, h):
                            area = calculate_area(x1, y1, x2, y2, x1, y2)
                            area += calculate_area(x1, y1, x2, y2, x2, y1)
                            if is_valid_triangle(x1, y1, x2, y2, x1, y2) or is_valid_triangle(x1, y1, x2, y2, x2, y1):
                                max_area = max(max_area, area)
        max_areas.append(int(max_area))

    return max_areas

