def reconstruct_sequence(n: int, b: List[int]) -> Optional[List[int]]:
    a = [0] * n
    for i in range(n):
        # Calculate the possible value of a_i
        possible_a_i = b[i] / (n - (i + 1) % n)
        # Check if the value is an integer and positive
        if possible_a_i > 0 and (b[i] % (n - (i + 1) % n)) == 0:
            a[i] = int(possible_a_i)
        else:
            return None

    # Verify the sequence
    for i in range(n):
        total_duration = 0
        for k in range(n):
            town = (i + k) % n
            total_duration += a[town] * (k + 2)
        if total_duration != b[i]:
            return None

    return a

