def func(grid: List[List[str]], n: int, m: int) -> List[List[str]]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m and grid[x][y] == '.'

    def can_force_to_lab(x: int, y: int) -> bool:
        visited = [[False] * m for _ in range(n)]
        queue = [(x, y)]
        visited[x][y] = True

        while queue:
            cx, cy = queue.pop(0)
            if (cx == 0 or cx == n - 1 or cy == 0 or cy == m - 1) and grid[cx][cy] == '.':
                return True

            directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
            for dx, dy in directions:
                nx, ny = cx + dx, cy + dy
                if is_valid(nx, ny) and not visited[nx][ny]:
                    visited[nx][ny] = True
                    queue.append((nx, ny))

        return False

    result_grid = [row[:] for row in grid]

    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.' and can_force_to_lab(i, j):
                result_grid[i][j] = '+'

    return result_grid

