def find_permutation(n: int, a: int, b: int) -> Optional[List[int]]:
    if a + b > n - 2 or abs(a - b) > 1:
        return None

    permutation = []
    if a >= b:
        # Start with a local maximum
        start_with_max = True
    else:
        start_with_max = False

    for i in range(n):
        if start_with_max:
            if i % 2 == 0:
                permutation.append(i // 2 + 1)
            else:
                permutation.append(n - (i // 2))
        else:
            if i % 2 == 0:
                permutation.append(n - (i // 2))
            else:
                permutation.append(i // 2 + 1)

    if a >= b:
        if start_with_max:
            # Ensure there are exactly a local maxima
            for i in range(2, n - 1, 2):
                if permutation[i] > permutation[i - 1] and permutation[i] > permutation[i + 1]:
                    a -= 1
                else:
                    break
            if a != 0:
                return None
        else:
            # Ensure there are exactly a local maxima
            for i in range(1, n - 1, 2):
                if permutation[i] > permutation[i - 1] and permutation[i] > permutation[i + 1]:
                    a -= 1
                else:
                    break
            if a != 0:
                return None
    else:
        if start_with_max:
            # Ensure there are exactly b local minima
            for i in range(2, n - 1, 2):
                if permutation[i] < permutation[i - 1] and permutation[i] < permutation[i + 1]:
                    b -= 1
                else:
                    break
            if b != 0:
                return None
        else:
            # Ensure there are exactly b local minima
            for i in range(1, n - 1, 2):
                if permutation[i] < permutation[i - 1] and permutation[i] < permutation[i + 1]:
                    b -= 1
                else:
                    break
            if b != 0:
                return None

    return permutation

