def func(t: int, test_cases: List[Tuple[int, int, List[Tuple[int, int, int]]]]) -> List[int]:
    results = []
    for n, k, mines in test_cases:
        # Create a set to store the unique detonated times
        detonated_times = set()

        # Add the initial times of all mines
        for x, y, timer in mines:
            detonated_times.add(timer)

        # Add the times of mines that can be detonated by others
        while True:
            new_detonations = set()
            for x, y, timer in mines:
                for dx in range(-k, k + 1):
                    for dy in range(-k, k + 1):
                        if (dx, dy) != (0, 0):
                            new_detonations.add((x + dx, y + dy, timer + 1))

            # Check for new mines to detonate
            for nx, ny, nt in new_detonations:
                if (nx, ny) not in detonated_times and all((abs(nx - mx) > k and abs(ny - my) > k) for mx, my, _ in mines):
                    detonated_times.add(nt)

            # If no new mines are added, break the loop
            if not new_detonations:
                break

        # The maximum time to detonate all mines is the result for this test case
        results.append(max(detonated_times))

    return results

