def find_original_array(N: int, output_array: List[int]) -> Optional[List[int]]:
    if N == 2:
        if output_array[0] == output_array[1]:
            return [output_array[0] // 2, output_array[0] // 2]
        else:
            return [output_array[0] - output_array[1], output_array[1]]

    original_array = [0] * N
    original_array[0] = (output_array[0] + output_array[1] - output_array[N-1]) // 2
    for i in range(1, N):
        original_array[i] = output_array[i] - original_array[0]

    # Check if the constructed array matches the output array
    for i in range(N):
        if original_array[i] + original_array[i-1] != output_array[i]:
            return None

    return original_array

