def check_bipartite(n: int, p: list) -> typing.Optional[list]:
    graph = [[] for _ in range(2 * n + 1)]
    for i in range(n):
        for j in range(i + 1, n):
            if p[i] > p[j]:
                graph[i + 1].append(j + n + 1)
                graph[j + 1].append(i + n + 1)
            elif p[i] < p[j]:
                graph[i + n + 1].append(j + 1)
                graph[j + n + 1].append(i + 1)

    color = [-1] * (2 * n + 1)
    def bfs(start: int):
        queue = deque([start])
        color[start] = 0
        while queue:
            node = queue.popleft()
            for neighbor in graph[node]:
                if color[neighbor] == -1:
                    color[neighbor] = 1 - color[node]
                    queue.append(neighbor)
                elif color[neighbor] == color[node]:
                    return False
        return True

    for i in range(1, 2 * n + 1):
        if color[i] == -1:
            if not bfs(i):
                return None

    a = []
    for i in range(1, n + 1):
        if color[i] == 0:
            a.append(p[i - 1])
        else:
            a.append(-p[i - 1])
    return a

