def find_minimum_operations(grid: List[List[int]]) -> int:
    def is_portal(x, y, size_x, size_y):
        for i in range(size_x):
            for j in range(size_y):
                if i > 0 and i < size_x - 1 and j > 0 and j < size_y - 1:
                    if grid[x + i][y + j] == 1:
                        return False
                else:
                    if grid[x + i][y + j] == 0:
                        return False
        return True

    n = len(grid)
    m = len(grid[0])
    operations = float('inf')

    # Iterate over all possible rectangle sizes
    for size_x in range(5, n + 1):
        for size_y in range(4, m + 1):
            for i in range(n - size_x + 1):
                for j in range(m - size_y + 1):
                    if is_portal(i, j, size_x, size_y):
                        # Calculate the number of operations needed
                        current_operations = (size_x * size_y) - (3 * 2)  # Subtract the corners that are already correct
                        operations = min(operations, current_operations)

    return operations if operations != float('inf') else 0

