def find_desert_pairs(N: int, edges: List[Tuple[int, int]]) -> int:
    # Initialize the graph and other necessary data structures
    graph = {i: [] for i in range(1, N + 1)}
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    # Function to find the connected components in the graph
    def find_components(graph):
        visited = set()
        components = []

        def dfs(node):
            stack = [node]
            component = []
            while stack:
                current = stack.pop()
                if current not in visited:
                    visited.add(current)
                    component.append(current)
                    for neighbor in graph[current]:
                        if neighbor not in visited:
                            stack.append(neighbor)
            return component

        for node in graph:
            if node not in visited:
                components.append(dfs(node))
        return components

    # Function to check if a graph is a cactus
    def is_cactus(graph, edges, component_edges):
        edge_index = {edge: i for i, edge in enumerate(edges)}
        visited = [False] * len(edges)
        stack = []

        for edge in component_edges:
            if not visited[edge_index[edge]]:
                stack.append(edge)
                while stack:
                    current = stack.pop()
                    current_index = edge_index[current]
                    if not visited[current_index]:
                        visited[current_index] = True
                        for neighbor in graph[current[0]]:
                            if (current[0], neighbor) != current and (neighbor, current[0]) != current:
                                neighbor_edge = (min(current[0], neighbor), max(current[0], neighbor))
                                if not visited[edge_index[neighbor_edge]]:
                                    stack.append(neighbor_edge)
                        for neighbor in graph[current[1]]:
                            if (current[1], neighbor) != current and (neighbor, current[1]) != current:
                                neighbor_edge = (min(current[1], neighbor), max(current[1], neighbor))
                                if not visited[edge_index[neighbor_edge]]:
                                    stack.append(neighbor_edge)
        return all(visited)

    # Main logic to find the number of desert pairs
    components = find_components(graph)
    edge_index = {edge: i for i, edge in enumerate(edges)}
    result = 0

    for i in range(len(components)):
        for j in range(i, len(components)):
            component_edges = set()
            for node in components[i]:
                for neighbor in graph[node]:
                    edge = (min(node, neighbor), max(node, neighbor))
                    if edge in edges:
                        component_edges.add(edge)
            for node in components[j]:
                for neighbor in graph[node]:
                    edge = (min(node, neighbor), max(node, neighbor))
                    if edge in edges:
                        component_edges.add(edge)
            if is_cactus(graph, edges, list(component_edges)):
                result += 1

    return result

