def find_lr(n: int) -> Tuple[int, int]:
    # Start with the smallest possible value for l
    l = -1
    r = 1
    current_sum = l + r

    # Use a loop to find the correct l and r
    while current_sum != n:
        if current_sum < n:
            r += 1
        else:
            l += 1
        current_sum = sum_of_range(l, r)

    return (l, r)

def sum_of_range(l: int, r: int) -> int:
    # Calculate the sum of the range from l to r
    return (r * (r + 1) // 2) - ((l * (l - 1) // 2))

