        for i in range(n - 1):
            if s[i:i+2] == 'ab':
                # Change the 'b' to 'a' to create 'ba'
                return s[:i] + 'ba' + s[i+1:]
    else:
        # If there are more 'ba' than 'ab', we need to add 'ab'
        for i in range(n - 1):
            if s[i:i+2] == 'ba':
                # Change the 'a' to 'b' to create 'ab'
                return s[:i] + 'ab' + s[i+1:]

