from typing import List

def find_reachable_cells(grid: List[List[str]], n: int, m: int) -> List[List[str]]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m and grid[x][y] == '.'

    def dfs(x: int, y: int):
        if not is_valid(x, y) or visited[x][y]:
            return
        visited[x][y] = True
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny) and not visited[nx][ny]:
                if (dx, dy) != (-dx, -dy):
                    dfs(nx, ny)

    lab_x, lab_y = -1, -1
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'L':
                lab_x, lab_y = i, j
                break
        if lab_x != -1:
            break

    visited = [[False] * m for _ in range(n)]
    dfs(lab_x, lab_y)

    for i in range(n):
        for j in range(m):
            if visited[i][j]:
                grid[i][j] = '+'
    return grid

