def find_largest_composite_subset(arr: List[int]) -> Tuple[int, List[int]]:
    n = len(arr)
    max_subset_size = 0
    best_subset_indices = []

    for mask in range(1, 1 << n):
        subset_sum = 0
        subset_indices = []
        for i in range(n):
            if mask & (1 << i):
                subset_sum += arr[i]
                subset_indices.append(i + 1)  # 1-indexed for output

