def find_permutation(n: int, a: int, b: int) -> Optional[List[int]]:
    if a + b > n - 2 or abs(a - b) > 1:
        return None

    permutation = []
    if a >= b:
        # Start with a local maximum
        i = 1
        while a > 0 and i < n:
            permutation.append(i)
            permutation.append(i + 1)
            i += 2
            a -= 1
        for i in range(n, 0, -1):
            if i not in permutation:
                permutation.append(i)
    else:
        # Start with a local minimum
        i = n
        while b > 0 and i > 1:
            permutation.append(i)
            permutation.append(i - 1)
            i -= 2
            b -= 1
        for i in range(1, n + 1):
            if i not in permutation:
                permutation.append(i)

    return permutation

