def find_min_operations(n, m, grid):
    def is_portal(top_left, bottom_right):
        # Check the borders of the rectangle
        for x in range(top_left[0], bottom_right[0] + 1):
            if grid[x][top_left[1]] != '0' or grid[x][bottom_right[1]] != '0':
                return False
        for y in range(top_left[1], bottom_right[1] + 1):
            if grid[top_left[0]][y] != '0' or grid[bottom_right[0]][y] != '0':
                return False
        return True

    def count_operations(top_left, bottom_right):
        operations = 0
        for x in range(top_left[0], bottom_right[0] + 1):
            for y in range(top_left[1], bottom_right[1] + 1):
                if grid[x][y] == '1':
                    operations += 1
        return operations

    min_operations = float('inf')
    for top_left_x in range(n - 4):
        for top_left_y in range(m - 3):
            for bottom_right_x in range(top_left_x + 4, n):
                for bottom_right_y in range(top_left_y + 3, m):
                    if is_portal((top_left_x, top_left_y), (bottom_right_x, bottom_right_y)):
                        operations = count_operations((top_left_x, top_left_y), (bottom_right_x, bottom_right_y))
                        min_operations = min(min_operations, operations)
    return min_operations if min_operations != float('inf') else -1

