            if roles[i] == -1 and roles[j] == -1:
                roles[i] = 1
                roles[j] = 0
            elif roles[i] == -1:
                roles[i] = 1 if roles[j] == 0 else 0
            elif roles[j] == -1:
                roles[j] = 1 if roles[i] == 0 else 0
            else:
                if roles[i] == roles[j]:
                    return -1  # Contradiction in the comments

        elif c == 'crewmate':
            if roles[i] == -1 and roles[j] == -1:
                roles[i] = 0
                roles[j] = 1
            elif roles[i] == -1:
                roles[i] = 0 if roles[j] == 1 else 1
            elif roles[j] == -1:
                roles[j] = 0 if roles[i] == 1 else 1
            else:
                if roles[i] == roles[j]:
                    return -1  # Contradiction in the comments

    # Count the maximum number of imposters
    imposters = sum(1 for role in roles if role == 1)
    return imposters

