def build_tree(edges: List[Tuple[int, int, str]], n: int) -> List[TreeNode]:
    nodes = [TreeNode(i) for i in range(n + 1)]
    for u, v, c in edges:
        nodes[u].children.append(nodes[v])
        nodes[v].children.append(nodes[u])
    return nodes

def find_occurrences(tree: List[TreeNode], strings: List[str], u: int, v: int, l: int, r: int) -> int:
    def find_path(node: TreeNode, target: int, path: List[int]) -> bool:
        if node.val == target:
            return True
        for child in node.children:
            if find_path(child, target, path):
                path.append(child.val)
                return True
        return False

    path = []
    find_path(tree[u], v, path)
    path_str = ''.join(str(node.val) for node in path)

    combined_strings = ''.join(strings[i] for i in range(l - 1, r))
    return combined_strings.count(path_str)

