def find_lr(n: int) -> tuple[int, int]:
    # Use the formula for the sum of an arithmetic series
    l = -1
    r = 1
    current_sum = 0

    while current_sum != n:
        if current_sum < n:
            r += 1
        else:
            l += 1
        current_sum = (r * (r + 1) // 2) - (l * (l - 1) // 2)

