def find_smallest_permutation(S: str, T: str) -> str:
    from collections import Counter

    # Count the occurrences of each character in T
    t_counter = Counter(T)

    # Initialize the result list with characters from S
    result = list(S)

    # Iterate over the result list and replace characters to ensure T is not a subsequence
    for i, char in enumerate(result):
        if t_counter[char] > 0:
            t_counter[char] -= 1
            result[i] = ''

    # Fill the remaining characters in the result list
    for i, char in enumerate(result):
        if char == '':
            for c in sorted(t_counter.keys()):
                if t_counter[c] > 0:
                    result[i] = c
                    t_counter[c] -= 1
                    break

    # Join the result list to form the final string
    return ''.join(result)

