def can_split_sequence(n: int, sequence: List[int]) -> str:
    def lis_length(subseq: List[int]) -> int:
        if not subseq:
            return 0
        dp = [1] * len(subseq)
        for i in range(1, len(subseq)):
            for j in range(i):
                if subseq[i] > subseq[j]:
                    dp[i] = max(dp[i], dp[j] + 1)
        return max(dp)

    xor_result = 0
    for i in range(n):
        subseq = []
        for j in range(i, n):
            subseq.append(sequence[j])
            xor_result ^= lis_length(subseq)

    return "YES" if xor_result == 0 else "NO"

