def find_perfect_sequences(n: int, M: int) -> int:
    # Function to calculate the factorial modulo M
    def factorial_mod(k: int) -> int:
        result = 1
        for i in range(1, k + 1):
            result = (result * i) % M
        return result

    # Function to calculate the number of perfect sequences
    def count_perfect_sequences(n: int, M: int) -> int:
        # Initialize the result
        result = 0

        # Iterate over all possible sequences
        for a1 in range(1, n + 2):
            for a2 in range(1, n + 2):
                for a3 in range(1, n + 2):
                    # Check if the sequence is perfect
                    if is_perfect([a1, a2, a3]):
                        result = (result + 1) % M
        return result

    # Function to check if a sequence is perfect
    def is_perfect(sequence: list[int]) -> bool:
        for subseq in get_subsequences(sequence):
            if not is_good(subseq):
                return False
        return True

    # Function to get all non-empty subsequences of a sequence
    def get_subsequences(sequence: list[int]) -> list[list[int]]:
        if len(sequence) == 0:
            return [[]]
        first, rest = sequence[0], sequence[1:]
        subsequences_without_first = get_subsequences(rest)
        subsequences_with_first = [[first] + subseq for subseq in subsequences_without_first]
        return subsequences_without_first + subsequences_with_first

    # Function to check if a subsequence is good
    def is_good(subseq: list[int]) -> bool:
        if len(subseq) == 0:
            return True
        max_val = max(subseq)
        min_val = min(subseq)
        sum_val = sum(subseq)
        return max_val * min_val >= sum_val

    # Calculate and return the number of perfect sequences
    return count_perfect_sequences(n, M)

