def determine_tree_configuration(n: int, m: int, edges: List[Tuple[int, int, int]], elves: List[Tuple[int, int, int]]) -> Optional[List[Tuple[int, int, int]]]:
    # Initialize the adjacency list for the tree
    tree = [[] for _ in range(n + 1)]
    for x, y, v in edges:
        tree[x].append((y, v))
        tree[y].append((x, v))

    # Initialize the list to store the tree configuration
    tree_config = [-1] * (n - 1)

    # Function to perform DFS and collect the path XOR values
    def dfs(node: int, parent: int, path_xor: int) -> Optional[int]:
        nonlocal tree_config
        if parent != -1:
            for i, (child, edge_value) in enumerate(tree[parent]):
                if child == node:
                    tree_config[i] = edge_value
                    break

        for child, edge_value in tree[node]:
            if child != parent:
                result = dfs(child, node, path_xor ^ edge_value)
                if result is not None:
                    return result
        if node == n:
            return path_xor
        return None

    # Start DFS from the root (node 1)
    dfs(1, -1, 0)

    # Check if the tree configuration is consistent with the elves' memories
    for (a, b, p) in elves:
        path_xor = 0
        current = a
        while current != b:
            for child, edge_value in tree[current]:
                if child != parent:
                    path_xor ^= edge_value
                    current = child
                    break
        if (path_xor % 2) != p:
            return None

    # Return the tree configuration if it is consistent
    return [(edges[i][:2], edges[i][2]) for i in range(n - 1)]

