        while stack:
            r, c = stack.pop()
            if (r, c) in visited:
                continue
            visited.add((r, c))
            directions = []
            if is_valid(r - 1, c):
                directions.append((r - 1, c))
            if is_valid(r + 1, c):
                directions.append((r + 1, c))
            if is_valid(r, c - 1):
                directions.append((r, c - 1))
            if is_valid(r, c + 1):
                directions.append((r, c + 1))
            if len(directions) == 0 and grid[r][c] == '.':
                return True
            for dr, dc in directions:
                if (dr, dc) not in visited:
                    stack.append((dr, dc))
        return False

