def generate_subsets(arr: List[int]) -> List[List[int]]:
    n = len(arr)
    subsets = []
    for i in range(1, 1 << n):
        subset = [arr[j] for j in range(n) if (i & (1 << j))]
        subsets.append(subset)
    return subsets

