def _dictsum(dicts: List[Dict[str, int]]) -> Dict[str, int]:
    """
    Sum values with the same key in the dictionaries. And return a new dictionary.

    This function takes a list of dictionaries and sums the values for each key across all dictionaries.
    If a key is missing in any of the dictionaries, it is treated as having a value of 0 for that dictionary.

    Args:
        dicts (List[Dict[str, int]]): A list of dictionaries to sum values from.

    Returns:
        Dict[str, int]: A new dictionary with summed values for each key.

    Example:
        dict1 = {'a': 1, 'b': 2}
        dict2 = {'a': 5, 'b': 0}
        result = _dictsum([dict1, dict2])
        # result will be {'a': 6, 'b': 2}
    """
    result = {}

    for current_dict in dicts:
        for key, value in current_dict.items():
            if key in result:
                result[key] += value
            else:
                result[key] = value

    return result

