from typing import List, Tuple

def min_erasures_to_palindrome(t: int, test_cases: List[Tuple[int, str]]) -> List[int]:
    def is_palindrome(s: str) -> bool:
        """Check if a string is a palindrome."""
        return s == s[::-1]

    def min_deletions_to_make_palindrome(s: str, char_to_remove: str) -> int:
        """Calculate the minimum deletions required to make the string a palindrome by removing occurrences of a specific character."""
        left, right = 0, len(s) - 1
        deletions = 0

        while left < right:
            if s[left] == s[right]:
                left += 1
                right -= 1
            elif s[left] == char_to_remove:
                deletions += 1
                left += 1
            elif s[right] == char_to_remove:
                deletions += 1
                right -= 1
            else:
                return float('inf')  # Impossible to make palindrome with this character

        return deletions

    results = []

    for n, s in test_cases:
        if is_palindrome(s):
            results.append(0)
            continue

        min_deletions = float('inf')
        unique_chars = set(s)

        for char in unique_chars:
            deletions = min_deletions_to_make_palindrome(s, char)
            min_deletions = min(min_deletions, deletions)

        results.append(min_deletions if min_deletions != float('inf') else -1)

    return results

