from typing import List, Tuple

def optimal_starting_position(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    """Determine the optimal starting position for the robot on a grid.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, str]]): A list of tuples, each containing
            the dimensions of the grid (n, m) and the command string s.

    Returns:
        List[Tuple[int, int]]: A list of tuples representing the optimal starting
            positions (row, column) for each test case.
    """
    results = []

    for n, m, commands in test_cases:
        # Initialize the best position and the maximum commands executed
        best_position = (1, 1)
        max_commands = 0

        # Check all possible starting positions
        for start_row in range(1, n + 1):
            for start_col in range(1, m + 1):
                current_row, current_col = start_row, start_col
                commands_executed = 0

                for command in commands:
                    if command == 'L':
                        if current_col > 1:
                            current_col -= 1
                            commands_executed += 1
                        else:
                            break
                    elif command == 'R':
                        if current_col < m:
                            current_col += 1
                            commands_executed += 1
                        else:
                            break
                    elif command == 'U':
                        if current_row > 1:
                            current_row -= 1
                            commands_executed += 1
                        else:
                            break
                    elif command == 'D':
                        if current_row < n:
                            current_row += 1
                            commands_executed += 1
                        else:
                            break

                # Update the best position if more commands were executed
                if commands_executed > max_commands:
                    max_commands = commands_executed
                    best_position = (start_row, start_col)

        results.append(best_position)

    return results

# Input handling
if __name__ == "__main__":
    t = int(input())
    test_cases = []
    for _ in range(t):
        n, m = map(int, input().split())
        s = input().strip()
        test_cases.append((n, m, s))

    result = optimal_starting_position(t, test_cases)
    for r in result:
        print(r[0], r[1])

