from typing import List, Tuple

def optimal_starting_position(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    """
    Determine the optimal starting position for the robot on a checkered board
    to execute the maximum number of commands without falling off the board.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, str]]): A list of tuples, each containing
            the dimensions of the board (n, m) and the command string s.

    Returns:
        List[Tuple[int, int]]: A list of tuples containing the optimal starting
        positions (r, c) for each test case.
    """
    results = []

    for n, m, commands in test_cases:
        # Initialize the starting position to (1, 1)
        best_row, best_col = 1, 1
        max_commands = 0

        # Simulate the robot's movement from each possible starting position
        for start_row in range(1, n + 1):
            for start_col in range(1, m + 1):
                current_row, current_col = start_row, start_col
                executed_commands = 0

                for command in commands:
                    if command == 'L':
                        if current_col > 1:
                            current_col -= 1
                            executed_commands += 1
                        else:
                            break
                    elif command == 'R':
                        if current_col < m:
                            current_col += 1
                            executed_commands += 1
                        else:
                            break
                    elif command == 'U':
                        if current_row > 1:
                            current_row -= 1
                            executed_commands += 1
                        else:
                            break
                    elif command == 'D':
                        if current_row < n:
                            current_row += 1
                            executed_commands += 1
                        else:
                            break

                # Update the best starting position if more commands were executed
                if executed_commands > max_commands:
                    max_commands = executed_commands
                    best_row, best_col = start_row, start_col

        results.append((best_row, best_col))

    return results

