    """Calculate the minimum number of operations required to convert a rectangle into a portal.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, List[str]]]): A list of test cases, each containing
            the dimensions of the rectangle and the grid representation.

    Returns:
        List[int]: A list of minimum operations for each test case.
    """
    results = []

    for n, m, grid in test_cases:
        min_operations = float('inf')

        # Check all possible portal sizes
        for a in range(5, n + 1):
            for b in range(4, m + 1):
                # We need to check every possible top-left corner for the portal
                for start_row in range(n - a + 1):
                    for start_col in range(m - b + 1):
                        operations = 0
                        # Check the conditions for the portal
                        # Check the borders
                        for x in range(1, a):
                            if grid[start_row + x][start_col] != '1':
                                operations += 1
                            if grid[start_row + x][start_col + b - 1] != '1':
                                operations += 1
                        for y in range(1, b):
                            if grid[start_row][start_col + y] != '1':
                                operations += 1
                            if grid[start_row + a - 1][start_col + y] != '1':
                                operations += 1
                        # Check the inner area
                        for x in range(1, a):
                            for y in range(1, b):
                                if grid[start_row + x][start_col + y] != '0':
                                    operations += 1
                        # Update the minimum operations found
                        min_operations = min(min_operations, operations)

        results.append(min_operations)

    return results

