    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, List[str]]]): A list of test cases, each containing
            the dimensions of the rectangle and the grid representation.

    Returns:
        List[int]: A list of minimum operations for each test case.
    """
    results = []

    for n, m, grid in test_cases:
        min_operations = float('inf')

        # Iterate over all possible portal dimensions
        for a in range(5, n + 1):
            for b in range(4, m + 1):
                # Check if the portal can fit in the grid
                if a <= n and b <= m:
                    operations = 0

                    # Count operations for the corners
                    if grid[0][0] == '0':  # M_{1,1}
                        operations += 1
                    if grid[0][b - 1] == '0':  # M_{1,b}
                        operations += 1
                    if grid[a - 1][0] == '0':  # M_{a,1}
                        operations += 1
                    if grid[a - 1][b - 1] == '0':  # M_{a,b}
                        operations += 1

                    # Count operations for the borders
                    for x in range(1, a - 1):
                        if grid[x][0] == '0':  # M_{x,1}
                            operations += 1
                        if grid[x][b - 1] == '0':  # M_{x,b}
                            operations += 1

                    for y in range(1, b - 1):
                        if grid[0][y] == '0':  # M_{1,y}
                            operations += 1
                        if grid[a - 1][y] == '0':  # M_{a,y}
                            operations += 1

                    # Count operations for the inner area
                    for x in range(1, a - 1):
                        for y in range(1, b - 1):
                            if grid[x][y] == '1':  # M_{x,y}
                                operations += 1

                    # Update minimum operations if this configuration is better
                    min_operations = min(min_operations, operations)

        results.append(min_operations)

    return results

# Input handling
