def min_operations_to_portal(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[int]:
    """Calculate the minimum number of operations required to convert a rectangle into a portal.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, List[str]]]): A list of test cases, each containing the dimensions
            of the rectangle and the grid representation.

    Returns:
        List[int]: A list of minimum operations for each test case.
    """
    results = []

    for n, m, grid in test_cases:
        min_operations = float('inf')

        # Iterate through all possible top-left corners of the sub-rectangle
        for start_row in range(n - 4):
            for start_col in range(m - 3):
                # Check all possible sizes of the sub-rectangle
                for height in range(5, n - start_row + 1):
                    for width in range(4, m - start_col + 1):
                        # Calculate the number of operations needed to convert this sub-rectangle into a portal
                        operations = 0
                        # Check the conditions for a portal
                        for x in range(height):
                            for y in range(width):
                                if x == 0 or x == height - 1 or y == 0 or y == width - 1:
                                    # Corners can be any type, so we skip them
                                    continue
                                if x == 0 or x == height - 1 or (y == 0 or y == width - 1):
                                    # First and last rows and columns can be any type
                                    continue
                                if grid[start_row + x][start_col + y] == '1':
                                    operations += 1  # Need to change obsidian to empty
                                elif grid[start_row + x][start_col + y] == '0':
                                    operations += 1  # Need to change empty to obsidian

                        # Check the vertical sides
                        for x in range(1, height - 1):
                            if grid[start_row + x][start_col] == '0':
                                operations += 1  # Need to change empty to obsidian
                            if grid[start_row + x][start_col + width - 1] == '0':
                                operations += 1  # Need to change empty to obsidian

                        # Check the horizontal sides
                        for y in range(1, width - 1):
                            if grid[start_row][start_col + y] == '0':
                                operations += 1  # Need to change empty to obsidian
                            if grid[start_row + height - 1][start_col + y] == '0':
                                operations += 1  # Need to change empty to obsidian

                        # Update the minimum operations found
                        min_operations = min(min_operations, operations)

        results.append(min_operations)

    return results

