from typing import List, Tuple, Dict

class Maze:
    def __init__(self, illusion_rates: List[int], edges: List[Tuple[int, int]]):
        self.illusion_rates = illusion_rates
        self.adjacency_list: Dict[int, List[int]] = self.build_adjacency_list(edges)

    def build_adjacency_list(self, edges: List[Tuple[int, int]]) -> Dict[int, List[int]]:
        """Builds an adjacency list from the given edges."""
        adjacency_list = {i: [] for i in range(1, len(self.illusion_rates) + 1)}
        for s, t in edges:
            adjacency_list[s].append(t)
            adjacency_list[t].append(s)
        return adjacency_list

    def update_illusion_rate(self, room: int, new_rate: int) -> None:
        """Updates the illusion rate of a specified room."""
        self.illusion_rates[room - 1] = new_rate

    def calculate_min_energy(self, start: int, end: int) -> int:
        """Calculates the minimum energy needed to travel between two rooms."""
        visited = [False] * (len(self.illusion_rates) + 1)
        return self.dfs(start, end, visited)

    def dfs(self, current: int, target: int, visited: List[bool]) -> int:
        """Performs depth-first search to find the path and calculate energy."""
        if current == target:
            return 0

        visited[current] = True
        min_energy = float('inf')

        for neighbor in self.adjacency_list[current]:
            if not visited[neighbor]:
                energy = self.calculate_energy(current, neighbor)
                total_energy = energy + self.dfs(neighbor, target, visited)
                min_energy = min(min_energy, total_energy)

        visited[current] = False
        return min_energy

    def calculate_energy(self, room_x: int, room_y: int) -> int:
        """Calculates the energy required to move between two rooms."""
        illusion_x = self.illusion_rates[room_x - 1]
        illusion_y = self.illusion_rates[room_y - 1]
        return max(abs(illusion_x + illusion_y), abs(illusion_x - illusion_y))


def main():
    n, q = map(int, input().split())  # Read number of rooms and queries
    illusion_rates = list(map(int, input().split()))  # Read initial illusion rates
    edges = [tuple(map(int, input().split())) for _ in range(n - 1)]  # Read edges
    maze = Maze(illusion_rates, edges)

    for _ in range(q):
        query = list(map(int, input().split()))
        if query[0] == 1:  # Update query
            _, u, c = query
            maze.update_illusion_rate(u, c)
        elif query[0] == 2:  # Energy query
            _, u, v = query
            result = maze.calculate_min_energy(u, v)
            print(result)

if __name__ == "__main__":
    main()

