from typing import List, Tuple

def optimal_starting_position(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    """
    Determine the optimal starting position for a robot on a grid to execute the maximum number of commands.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, str]]): A list of tuples, each containing:
            - n (int): The number of rows in the grid.
            - m (int): The number of columns in the grid.
            - s (str): The sequence of commands.

    Returns:
        List[Tuple[int, int]]: A list of tuples, each containing the optimal starting position (row, column) for each test case.
    """
    results = []

    for n, m, commands in test_cases:
        # Initialize the best position and the maximum commands executed
        best_position = (1, 1)
        max_commands_executed = 0

        # Check each possible starting position
        for start_row in range(1, n + 1):
            for start_col in range(1, m + 1):
                current_row, current_col = start_row, start_col
                commands_executed = 0

                for command in commands:
                    if command == 'L':
                        if current_col > 1:
                            current_col -= 1
                            commands_executed += 1
                        else:
                            break
                    elif command == 'R':
                        if current_col < m:
                            current_col += 1
                            commands_executed += 1
                        else:
                            break
                    elif command == 'U':
                        if current_row > 1:
                            current_row -= 1
                            commands_executed += 1
                        else:
                            break
                    elif command == 'D':
                        if current_row < n:
                            current_row += 1
                            commands_executed += 1
                        else:
                            break

                # Update the best position if the current one is better
                if commands_executed > max_commands_executed:
                    max_commands_executed = commands_executed
                    best_position = (start_row, start_col)

        results.append(best_position)

    return results

