class Maze:
    def __init__(self, n: int, illusion_rates: List[int], edges: List[Tuple[int, int]]):
        self.n = n
        self.illusion_rates = illusion_rates
        self.adjacency_list: Dict[int, List[int]] = {i: [] for i in range(1, n + 1)}
        self.build_tree(edges)

    def build_tree(self, edges: List[Tuple[int, int]]) -> None:
        """Builds the tree structure using adjacency lists."""
        for s, t in edges:
            self.adjacency_list[s].append(t)
            self.adjacency_list[t].append(s)

    def update_illusion_rate(self, room: int, new_rate: int) -> None:
        """Updates the illusion rate of a specific room."""
        self.illusion_rates[room - 1] = new_rate

    def calculate_energy(self, u: int, v: int) -> int:
        """Calculates the minimum energy required to travel from room u to room v."""
        visited = [False] * (self.n + 1)
        return self.dfs(u, v, visited)

    def dfs(self, current: int, target: int, visited: List[bool]) -> int:
        """Performs DFS to find the energy cost from current to target room."""
        if current == target:
            return 0

        visited[current] = True
        min_energy = float('inf')

        for neighbor in self.adjacency_list[current]:
            if not visited[neighbor]:
                energy_cost = self.energy_between(current, neighbor)
                total_energy = energy_cost + self.dfs(neighbor, target, visited)
                min_energy = min(min_energy, total_energy)

        visited[current] = False
        return min_energy

    def energy_between(self, u: int, v: int) -> int:
        """Calculates the energy required to travel between two rooms."""
        return max(abs(self.illusion_rates[u - 1] + self.illusion_rates[v - 1]),
                   abs(self.illusion_rates[u - 1] - self.illusion_rates[v - 1]))

