from typing import List, Tuple

class Flower:
    """Class to manage the growth of a flower based on watering schedule."""

    def __init__(self, initial_height: int = 1):
        """Initialize the flower with a given height and last watered day."""
        self.height = initial_height
        self.last_watered_day = -1

    def water(self, day: int):
        """Water the flower on a specific day and update its height."""
        if day - self.last_watered_day == 1:
            self.height += 5  # Watered on consecutive days
        else:
            self.height += 1  # Watered after a gap
        self.last_watered_day = day

    def check_death(self, current_day: int) -> bool:
        """Check if the flower has died based on the watering schedule."""
        return self.last_watered_day == -1 or (current_day - self.last_watered_day > 1 and self.last_watered_day - current_day == 1)

def flower_growth(t: int, test_cases: List[Tuple[int, List[int]]], initial_height: int = 1) -> List[int]:
    """Simulate the growth of flowers based on multiple test cases.

    Args:
        t (int): Number of test cases.
        test_cases (List[Tuple[int, List[int]]]): List of test cases with days and watering schedule.
        initial_height (int): Initial height of the flower.

    Returns:
        List[int]: Final heights of the flowers or -1 if they died.
    """
    results = []

    for n, watering_days in test_cases:
        flower = Flower(initial_height)

        for day in range(n):
            if watering_days[day] == 1:
                flower.water(day)
            if flower.check_death(day):
                results.append(-1)
                break
        else:
            results.append(flower.height)

    return results

