from typing import List, Tuple

def compare_numbers(t: int, test_cases: List[Tuple[Tuple[int, int], Tuple[int, int]]]) -> List[str]:
    """Compares two numbers represented by a base integer and a power of ten.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[Tuple[int, int], Tuple[int, int]]]): A list of tuples, where each tuple contains
            two tuples representing the two numbers to compare. Each inner tuple contains an integer and its
            corresponding power of ten.

    Returns:
        List[str]: A list of comparison results for each test case, where each result is one of '<', '>', or '='.
    """
    results = []

    for (x1, p1), (x2, p2) in test_cases:
        # Adjust the numbers by their powers of ten
        if p1 > p2:
            # x1 has more zeros, so we can compare x1 with x2 * 10^(p1 - p2)
            comparison = x1 * (10 ** (p1 - p2))
            if comparison > x2:
                results.append('>')
            elif comparison < x2:
                results.append('<')
            else:
                results.append('=')
        elif p1 < p2:
            # x2 has more zeros, so we can compare x2 with x1 * 10^(p2 - p1)
            comparison = x2 * (10 ** (p2 - p1))
            if comparison > x1:
                results.append('<')
            elif comparison < x1:
                results.append('>')
            else:
                results.append('=')
        else:
            # p1 == p2, we can compare x1 and x2 directly
            if x1 > x2:
                results.append('>')
            elif x1 < x2:
                results.append('<')
            else:
                results.append('=')

    return results

