def min_operations_to_portal(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[int]:
    """Calculate the minimum number of operations required to convert a rectangle into a portal.

    Args:
        t (int): The number of test cases.
        test_cases (List[Tuple[int, int, List[str]]]): A list of test cases, each containing the dimensions
            of the rectangle and the rectangle itself represented as a list of strings.

    Returns:
        List[int]: A list of integers representing the minimum operations for each test case.
    """
    results = []

    for n, m, grid in test_cases:
        min_operations = float('inf')

        # Check all possible sub-rectangles of size a x b where a >= 5 and b >= 4
        for a in range(5, n + 1):
            for b in range(4, m + 1):
                for i in range(n - a + 1):
                    for j in range(m - b + 1):
                        # Extract the sub-rectangle
                        sub_rectangle = [row[j:j + b] for row in grid[i:i + a]]
                        operations = calculate_operations_to_portal(sub_rectangle, a, b)
                        min_operations = min(min_operations, operations)

        results.append(min_operations)

    return results

def calculate_operations_to_portal(sub_rectangle: List[str], a: int, b: int) -> int:
    """Calculate the number of operations needed to convert a given sub-rectangle into a portal.

    Args:
        sub_rectangle (List[str]): The sub-rectangle represented as a list of strings.
        a (int): The number of rows in the sub-rectangle.
        b (int): The number of columns in the sub-rectangle.

    Returns:
        int: The number of operations required to convert the sub-rectangle into a portal.
    """
    operations = 0

    # Check the conditions for the portal
    # 1. Check the borders
    for x in range(1, a - 1):
        if sub_rectangle[x][0] != '1':
            operations += 1  # Left border
        if sub_rectangle[x][b - 1] != '1':
            operations += 1  # Right border

    for y in range(1, b - 1):
        if sub_rectangle[0][y] != '1':
            operations += 1  # Top border
        if sub_rectangle[a - 1][y] != '1':
            operations += 1  # Bottom border

    # 2. Check the inner area
    for x in range(1, a - 1):
        for y in range(1, b - 1):
            if sub_rectangle[x][y] != '0':
                operations += 1  # Inner area should be empty

    return operations

