import platform
import ctypes

def _c_optimizations_required() -> bool:
    """Check if C optimizations are required based on system capabilities and configuration."""

    # Check if the system supports SSE
    try:
        cpu_info = _get_cpu_info_from_cpuid()
        sse_support = "sse" in cpu_info.get("flags", [])
    except Exception:
        sse_support = False
    if not sse_support:
        return False

    # Check if the system has a 64-bit architecture
    arch_64bit = platform.architecture()[0] == "64bit"
    if not arch_64bit:
        return False

    # Check if the compiler supports C99
    try:
        c99_support = platform.libc_ver()[0] == "glibc" and int(platform.libc_ver()[1]) >= 2
    except NotImplementedError:
        c99_support = False
    if not c99_support:
        return False

    # Check if the system has the necessary libraries for C optimizations
    necessary_libraries = ["libm", "libgcc_s"]
    for lib in necessary_libraries:
        if not _check_library(lib):
            return False

    return True

def _get_cpu_info_from_cpuid() -> dict:
    """Get CPU info using ctypes and cpuid instruction."""
    class CPUID(ctypes.Structure):
        _fields_ = [("eax", ctypes.c_uint32),
                    ("ebx", ctypes.c_uint32),
                    ("ecx", ctypes.c_uint32),
                    ("edx", ctypes.c_uint32)]

    def cpuid(eax):
        cpu = CPUID()
        ctypes.cdll.util.__cpuid(ctypes.byref(cpu), eax)
        return cpu.eax, cpu.ebx, cpu.ecx, cpu.edx

    # Get CPUID info
    eax, ebx, ecx, edx = cpuid(0x00000001)
    flags = []
    if edx & (1 << 25):  # SSE
        flags.append("sse")
    # Add more flags as needed
    return {"flags": flags}

def _check_library(library_name: str) -> bool:
    """Check if a specific library is available on the system."""
    try:
        ctypes.CDLL(library_name)
        return True
    except OSError:
        return False

