def round_half_to_even(n: float) -> int:
    """Rounds a floating-point number to the nearest even integer if the value is exactly halfway between two integers.

    Args:
        n (float): The floating-point number to be rounded.

    Returns:
        int: The rounded integer, preferring the nearest even number when equidistant.
    """
    # Check if the number is exactly halfway between two integers
    if abs(n - round(n)) == 0.5:
        # Extract the integer part of the number
        int_part = int(round(n))
        # Determine if the integer part is odd or even
        if int_part % 2 == 0:
            return int_part
        else:
            # If the integer part is odd, we need to check the sign of the number
            if n > 0:
                return int_part + 1
            else:
                return int_part - 1
    else:
        # If the number is not exactly halfway, use the standard rounding
        return round(n)

