from typing import List

def string_to_int(string: str, alphabet: List[str]) -> int:
    """将一个字符串转换为整数，使用给定的字母表。

    :param string: 由alphabet中的字母组成的字符串
    :param alphabet: 字母列表
    :return: int, 根据给定的转换规则对应的数字
    """
    if not string:
        raise ValueError("Input string cannot be empty")

    if len(set(string)) != len(string):
        raise ValueError("Input string contains duplicate characters")

    if len(set(alphabet)) != len(alphabet):
        raise ValueError("Alphabet contains duplicate characters")

    char_to_index = {char: index for index, char in enumerate(alphabet)}

    if any(char not in char_to_index for char in string):
        raise ValueError("Input string contains characters not in alphabet")

    result = 0
    for char in string:
        result = result * len(alphabet) + char_to_index[char]

    return result


