def is_square(s: str) -> bool:
    """Check if the given string is a square string.

    Args:
        s (str): The input string to check.

    Returns:
        bool: True if the string is square, False otherwise.
    """
    # A string is square if it can be expressed as some string written twice in a row
    length = len(s)
    for i in range(1, length // 2 + 1):
        if length % i == 0:
            prefix = s[:i]
            if prefix * (length // i) == s:
                return True
    return False


def main():
    """Main function to handle input and output."""
    n = int(input())
    for _ in range(n):
        s = input()
        result = is_square(s)
        if result:
            print('YES')
        else:
            print('NO')


if __name__ == "__main__":
    main()

